mapper.select:
SELECT COUNT(*)
FROM TBL_CHAT_MESSAGE_READ
WHERE CHAT_MESSAGE_ID = #{chatMessageId}
AND USER_ID = #{userId}

SELECT COUNT(*)
FROM TBL_CHAT_MESSAGE tcm
WHERE tcm.CHAT_ROOM_ID = #{chatRoomId}
AND NOT EXISTS (
	SELECT 1
	FROM TBL_CHAT_MESSAGE_READ tcmr 
	WHERE tcmr.CHAT_MESSAGE_ID = tcm.CHAT_ROOM_ID
	AND tcmr.USER_ID = #{userId}
)

SELECT COUNT(*)
FROM TBL_CHAT_MESSAGE_READ
WHERE CHAT_MESSAGE_ID = #{chatMessageId}
